class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        //Sorting intervals 
        sort(intervals.begin(), intervals.end());

		stack<vector<int>> st;

        for(auto x: intervals){
            //This is case where the *upcoming interval covers our stored intervals*
            while(!st.empty() && st.top()[0] >= x[0] && st.top()[1] <= x[1]){
                st.pop();
            }

            //This is case where *upcoming interval is smaller and gets covered by 
            //already stored top interval*
            if(!st.empty() && st.top()[0] <= x[0] && st.top()[1] >= x[1]) continue;

            //If the upcoming interval is non-intersecting hence,
            st.push(x);
        }

        return st.size();
    }
};
