class Solution {
public:
    // Time Complexity:- O(NlogN)
    // Space Complexity:- O(1)
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        // sort intervals on the basis of l, and if l is same, then decreasing r
        sort(intervals.begin(),intervals.end(),[&](const vector<int>& p1,const vector<int>& p2){
            if(p1[0]==p2[0]){
                return p1[1]>p2[1];
            }
            return p1[0]<p2[0];
        });

        int n = intervals.size();
        int ans = n,rmax = intervals[0][1];
        for(int i=1;i<n;i++){
            // track rmax, if rmax>=curr r of interval, this interval is covered
            if(rmax>=intervals[i][1]){
                ans--;
            }
            else{
                rmax = intervals[i][1];
            }
        }

        return ans;
    }
};
