class Solution {
    public int removeCoveredIntervals(int[][] intervals) {
       Arrays.sort(intervals,(a,b)-> a[0]-b[0]);
        int i=0;
        int j=1;
        int ans=0;
        while(j<intervals.length){
            int a=intervals[i][0];
            int b=intervals[i][1];
            int c=intervals[j][0];
            int d=intervals[j][1];

            //if ith interval covers jth interval...............
            if(c>=a && b>=d){
                intervals[j]=intervals[i];
                ans++;
                i=j;
            // if jth interval cover ith interval...............
            }else if(c==a && b<=d){
                ans++;
               i=j; 
             // no cover....................   
            }else{
                i++;
            }

            j++; 
        }
        return intervals.length-ans;
    }
