class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        int n = intervals.size();

        // if start time is different then one with lesser start time will come first
        // if start time is same then the one with larger end time will come first

        sort(intervals.begin(), intervals.end(), [&](vector<int> &i1, vector<int> &i2) {
            if(i1[0] == i2[0]) return i1[1] > i2[1];
            else return i1[0] < i2[0];
        });
        int maxEnd = -1;
        int cnt = 0;
        for(int i = 0; i < n; i++) {
            if(maxEnd >= intervals[i][1]) {
                cnt++;
            } 
            maxEnd = max(maxEnd, intervals[i][1]);
        }
        return n - cnt;
    }
};
```

