class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        sort(intervals.begin(),intervals.end());
        int begin = INT_MAX;
        int end = INT_MIN;
        begin = min(begin,intervals[0][0]);
        end = max(end,intervals[0][1]);
        vector<vector<int>>interim;
        interim.push_back({begin,end});
        for(int i = 1;i<intervals.size();i++)
        {
           int val1 = intervals[i][0];
           int val2 = intervals[i][1];
           if(val1>=interim.back()[0] and val2<=interim.back()[1])
               continue;

           else if(val1<=interim.back()[0] and val2>interim.back()[1])
           {
               interim.back()[0] = val1;
               interim.back()[1] = val2;
           }
              else if(val1>=interim.back()[0] and val2>interim.back()[1])
               interim.push_back({val1,val2});
        }
        int n = interim.size();
        return n;

    }
};
