public int removeCoveredIntervals1(int[][] intervals) {

	int len = intervals.length, rem = len;
	for (int i = 0; i < len; i++) {
		for (int j = 0; j < len; j++) {
			if (i == j)
				continue;
			if (overlap(intervals[i], intervals[j])) {
				rem--;
				break;
			}
		}
	}
	return rem;
}

// Overlapping
// O(1) O(1)
public boolean overlap(int[] cand, int[] interval) {
	return (interval[0] <= cand[0] && cand[1] <= interval[1]);
}

