class Solution {
public:
    static bool comp(pair<int,int> a,pair<int,int> b) {
          if(a.first==b.first) return a.second>b.second;
          else return a.first<b.first;
    }
    int removeCoveredIntervals(vector<vector<int>>& in) {
          vector<pair<int,int>> px;
          for(int i=0; i<in.size(); i++) px.push_back(make_pair(in[i][0],in[i][1]));
          sort(px.begin(),px.end(),comp);
          pair<int,int> cur=px.front();
          int del=0;
          for(int i=1; i<px.size(); i++) {
                 del+=(cur.first<=px[i].first && px[i].second<=cur.second);
                 cur=make_pair(min(px[i].first,cur.first),max(cur.second,px[i].second)); 
          }
        return (int)in.size()-del; 
    }
};
