          return a[0] - b[0];
     });
        int beg = -1;
        int end = -1;
        int res = 0;
        for(int i=0 ; i<intervals.length ; i++){
          if(intervals[i][0] >beg && intervals[i][1]> end){
            beg = intervals[i][0];
            res++;
          }
          end = Math.max(end , intervals[i][1]);
        }
