```swift
class Solution {
    func divide(_ dd: Int, _ dr: Int) -> Int {
        let dd64 = Int64(abs(dd)), dr64 = Int64(abs(dr))
        var val: Int64 = 0, lhs: Int64 = 1, rhs = dd64
        while lhs <= rhs {
            let mid = (lhs + rhs) / 2
            if mid * dr64 <= dd64 {
                val = mid
                lhs = mid + 1
            } else {
                rhs = mid - 1
            }
        }
        return max(min(Int((dd * dr) < 0 ? -val : val), Int(Int32.max)), Int(Int32.min))
    }
}
```

