class Solution {
    public int sumNumbers(TreeNode root) {
        int component = 0;    // stores current_sum till leaf
		int ans = 0;     // stores sum of every number generated from root to leaf path
        return dfs(root, component, ans);
    }

    public int dfs(TreeNode root, int comp, int ans){
        if(root == null) return 0;
        comp = comp * 10 + root.val;    // updating the comp 
        if(root.left == null && root.right == null){
            ans += comp;    // updating the sum if the curr_node is a root
            return ans;
        }
        int call1 = dfs(root.left, comp, ans);    // sum of numbers to all leaf nodes on the left
        int call2 = dfs(root.right, comp, ans);  // // sum of numbers to all leaf nodes on the right
        return call1 + call2;
    }
}
