class Solution {
    public int sumNumbers(TreeNode root) {
        int res = 0;
        Queue<Pair<TreeNode,Integer>> q = new LinkedList<>();
        q.offer(new Pair<>(root,root.val));
        while(!q.isEmpty()){
            int s = q.size();
            for(int i = 0;i<s;i++){
                Pair<TreeNode,Integer> pair = q.poll();
                root = pair.getKey();
                int value = pair.getValue();
                if(root.left==null && root.right==null){
                    res += value;
                    continue;
                }
                if(root.left!=null){
                    q.offer(new Pair<>(root.left,value*10+root.left.val));
                }
                if(root.right!=null){
                    q.offer(new Pair<>(root.right,value*10+root.right.val));
                }
            }
        }

        return res;
    }
}
