    let totalSum = 0;
    if (root === null || root.val === undefined) return 0;
    if (root.left === undefined && root.right === undefined) return root.val;
    let queue = [root];

    while (queue.length > 0) {
        node = queue.shift();
        if (node.left === null && node.right == null) {
            totalSum += node.val;
