public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        sort(intervals.begin(),intervals.end());
        pair<int,int> curr;
        int c=1;
        curr.first=intervals[0][0];
        curr.second=intervals[0][1];
        for(int i=1;i<intervals.size();i++){
            if((curr.first<=intervals[i][0] && curr.second>=intervals[i][1]) || (curr.first>=intervals[i][0] && curr.second<=intervals[i][1])){
