class Solution {
public:
   //[[1,2],[3,4],[5,9],[5,7],[5,6],[6,9]]
   //[[1,6], [1,4]]


    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        int n = intervals.size();
        auto thisway = [&](vector<int> a , vector<int> b){
            return (a[0] < b[0] || (a[0] == b[0] && a[1] > b[1]));
        };
        sort(intervals.begin() , intervals.end(), thisway);
        vector<int> cur = intervals[0];
        int total = n;
        for(int i = 1; i < n; ++i){             // first we are sorting by left of the each interval in increasing order and 
            if(cur[1] >= intervals[i][1]){      // if equal lefts are there then by right in decreasing order
                --total;                        // then we are basically initially choosing a interval and then checking how many further
            }                                   // interval are its subsets otherwise take this new interval as we have already sorted 
            else{                               // by first element so it means either this interval that we are checking is
                cur = intervals[i];             // has range outside our tillnow choosen interval 
            }                                   // so we decide to update our choosen interval to this one.
        }                                       // then same goes on... 
        return total;                           // [[2,4],[2,3],[3,6],[3,4],[3,3][4,9],[4,3]]   
    }                                           // [[6,9],[6,8],[6,7],[7,8],[9,14],[9,13],[10,12],[12,15]]

};                                              

