class intervalsComparator implements Comparator<int[]> {
    @Override
    public int compare(int[] entry1, int[] entry2) {
		// sort acc to ascending start of interval
		//if start is same then sort acc to descending end of interval
        if (entry1[0] > entry2[0]) {
            return 1;
        } else if(entry1[0] < entry2[0]) {
            return -1;
        } else if(entry1[1] > entry2[1]) {
            return -1;
        } else if(entry1[1] < entry2[1]) {
            return 1;
        } else {
            return -1;
        }
    }
}

class Solution {
    public int removeCoveredIntervals(int[][] intervals) {

        Arrays.sort(intervals, new intervalsComparator());
		//max ending yet
        int maxEnd = intervals[0][1];
		//max case, all intervals are individual and included
        int res = intervals.length;
        for(int i = 1; i < intervals.length; i++) {

			//this interval's start is higher than prev(since, ascending order)

            if(intervals[i][1] <= maxEnd) { 
			//this interval's end time is lesser than max end time encountered yet
			// this will be absorbed, so reduce total individual intervals
                res--; 
            }
			// keep a track on max end time encountered for future intervals
            maxEnd = Math.max(maxEnd, intervals[i][1]); 
        }

        return res;
    }
}
