class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) 
    {

        int n = intervals.size();
        int ans=0;
        for(int i=0;i<n;++i)
        {
            int s = intervals[i].front();       //start point of a interval
            int e = intervals[i].back();        //end point
            int c = 0;
            for(int  j = 0;j<n;++j)
            {
                int a = intervals[j].front();       //start 
                int b = intervals[j].back();        //end

                if(s>=a && b>=e)
                    ++c;
            }
            if(c>1)                 //greater than one because c=1 will count the same iterval also
                ++ans;              //count extra interval present
        }
        return n-ans;               //return remaining no of interval

    }
};
