class Solution {
    public int removeCoveredIntervals(int[][] intervals) {

        // Sort the intervals (l, r) based on 'l' value. 
        // If two intervals have same 'l' value sort them based on highest 'r' value.
        Arrays.sort(intervals, (interval1, interval2) -> {
            if (interval1[0] != interval2[0]) {
               return interval1[0] - interval2[0]; 
            } else {
                return interval2[1] - interval1[1]; 
            }

        });

        int removed = 0;

        // Start with reference interval.
        int[] referenceInterval = intervals[0];

        for (int i = 1; i < intervals.length; i++) {
            // Check if each interval is mergable by comparing it with our reference interval.
            // If the current interval is with in our reference interval merge the current interval
            // to reference interval.
            if (intervals[i][0] >= referenceInterval[0] && intervals[i][1] <= referenceInterval[1]) {
                // note that as part of merging there is no need to update the right value into our 
                // reference interval as right value in reference interval is already the highest value.
                referenceInterval[0] =  intervals[i][0];
                removed++;
                continue;
            }
            // If not make current interval as our reference interval.
            referenceInterval = intervals[i];
        }

        // Return remaining intervals.
        return intervals.length - removed;
    }
}

