class Solution {
public:
    bool static com(vector<int>&a,vector<int>&b)
    {
        if(a[0]==b[0])
        {
            return a[1]>b[1];
        }
        return a[0]<b[0];
    }
    int removeCoveredIntervals(vector<vector<int>>& box) 
    {
        //diamond notes
        //sort as mentioned above 
        //and then make 2 pointers i=1,j=1,and check if condtions satisfy then do i++ until it is satisfied and then do c++ and put some conditons for edge cases.
        sort(box.begin(),box.end(),com);
        int c=0;
        int i=1;
        int j=0;
        bool ch=true;
        while(i<box.size() && j<box.size())
        {
            while(i<box.size() && box[j][0]<=box[i][0] && box[j][1]>=box[i][1])
            {
                ch=false;
                i++;
            }
            c++;
            j=i;
            i++;
        }
        if(j<box.size())c++;
        if(c==0)return 1;
        if(ch)return box.size();
        return c;
    }
};
