class Solution {
    class Pair implements Comparable<Pair>{
        public int x, y;

        public Pair(int a, int b){
            this.x = a;
            this.y = b;
        }

        public int compareTo(Pair p){
            if(this.x == p.x)
                return p.y - this.y;   // if x matches then sort in descending order of y
            else
                return this.x - p.x;   // else sort in ascending order of x
        }
    }

    public int removeCoveredIntervals(int[][] intervals) {
        int N = intervals.length;
        int i;
        PriorityQueue<Pair> queue = new PriorityQueue<Pair>();

        for(i = 0; i < N; i++){
            queue.offer(new Pair(intervals[i][0], intervals[i][1]));
        }

        Pair latest = new Pair(Integer.MIN_VALUE, Integer.MIN_VALUE);
        int count = 0;
        Pair p = null;

        while(!queue.isEmpty()){
            p = queue.poll();
            // check if p is NOT inside latest
            if(count == 0 || !(latest.x <= p.x && latest.y >= p.y)){
                latest = p;
                ++count;
            }
        }

        return count; 
    }
}
