class Solution:

    def removeCoveredIntervals(self, intervals: List[List[int]]) -> int:
        intervals.sort(key=lambda x:(x[0], -1*x[1]))

        left = intervals[0][0]
        right = intervals[0][1]

        res = 0

        for i in range(1, len(intervals)):
            intv = intervals[i]

            #covered intervals
            if left <= intv[0] and right >= intv[1]:
                res += 1

            #intersected intervals
            if right >= intv[0] and right <= intv[1]:
                right = intv[1]

            #neither covered nor intersected
            if right < intv[0]:
                left = intv[0]
                right = intv[1]

        return len(intervals) - res

