class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        vector<vector<int>>& v = intervals;
        sort(v.begin(), v.end(), [](auto r, auto l) {
			return r[0]!=l[0]?r[0]<l[0]:r[1]>l[1] ;
		}); 

        int n = v.size();
        int cnt = 0;
        vector<bool> covered(n,false);
        for(int i = 0; i<n; i++) {
            int start =v[i][0];
            int end = v[i][1];
            int j = i+1;
            while(j<n && v[j][0]<=end) {
                if(v[j][1]<=end && !covered[j]) {
                    covered[j] = true;   
                    cnt++;
                }
                j++;
            }
        }
        return n-cnt;
    }
};
