        vector<vector<int>>& v = intervals;
        sort(v.begin(), v.end(), [](auto r, auto l) {
			return r[0]!=l[0]?r[0]<l[0]:r[1]>l[1] ;
		}); 
		// [[1,2],[1,4],[3,4]] will be sorted into [[1,4],[1,2],[3,4]]

        int n = v.size();
        int cnt = 0;
        vector<bool> covered(n,false);
        for(int i = 0; i<n; i++) {
            int start =v[i][0];
            int end = v[i][1];
            int j = i+1;
			//for each interval_i [start_i, end_i], 
			//we can run the following loop
            while(j<n && v[j][0]<=end) {
				//for all j that start_i <= start_j <= end_i, run the loop for j here. 
				//becase start_j  have already in interval_i, so we only need to check if end_j ini nterval_i
				//if it is, then count up. 
                if(v[j][1]<=end && !covered[j]) {
                    covered[j] = true;   
					//set covered[j] as true so interval only can be counted when first time to be covered.
                    cnt++;
                }
                j++;
            }
        }
        return n-cnt;
    }
};
