class Solution {
public:
    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        //sort the intervals

        sort(intervals.begin(),intervals.end());
        int i=0;
        int j=1;
        int len=intervals.size();
        int removed=0;
        while(j<len)
        {
            //check whether interval i is covered by interval j
            if(intervals[j][0]<=intervals[i][0] && intervals[i][1]<=intervals[j][1])
            {
                //remove interval i since it is covered
                removed++;
                i=j;
                j++;
            }
            //check if interval j is covered by interval i
            else if(intervals[i][0]<=intervals[j][0] && intervals[j][1]<=intervals[i][1])
            {
                //remove interval j
                removed++;
                j++;
            }
            else
            {
                //if none of them have covered by each other
                i=j;
                j++;
            }
        }

        //return the remaining intervals
        return len-removed;
    }
};
```
