class Solution {
public:
    // IF START IS EQUAL THEN ARRANGE ON THE BASIS OF END VALUE AND PUT PAIR WITH MORE END VALUE FIRST
    struct comp{
        bool operator()(pair<int,int> p1,pair<int,int> p2){
            if(p1.first==p2.first)
                return p1.second<p2.second;
            return p1.first>p2.first;
        }
    };

    int removeCoveredIntervals(vector<vector<int>>& intervals) {
        priority_queue<pair<int,int>,vector<pair<int,int>>,comp> pq;

        for(int i=0;i<intervals.size();i++)pq.push({intervals[i][0],intervals[i][1]});

        int x=0;                      // CALCULATE NO OF INTERVALS WHICH ARE TO BE REMOVED
        int start=pq.top().first;
        int end=pq.top().second;
        pq.pop();

        while(pq.size()!=0){
            pair<int,int> p=pq.top();pq.pop();
            if(p.first>=start && p.second<=end)x++;  // IF INTERVAL RESIDES IN THE BOUNDARY WE CREATED INCREASE X
            start=min(start,p.first);
            end=max(end,p.second);
        }
        cout<<x;
        return intervals.size()-x;  //RETURN ANSWER
    }
};

