            return grid[0][0];

        vector<vector<int>>dp(n, vector<int>(n));

        for(int j = 0; j < n; j++)
            dp[0][j] = grid[0][j];
        for(int i = 1; i < n; i++){
            for(int j = 0; j < n; j++){
