class Solution {
    public int minFallingPathSum(int[][] arr) {
        int m = arr.length, n = arr[0].length, dp[][] = new int[m][2], min[][] = new int[m][2];
        for (int i = 0; i < m; i++) {
            min[i] = getMin(arr[i]);
            if (i == 0) {dp[i] = new int[] {arr[i][min[i][0]], arr[i][min[i][1]]}; continue;}
            for (int j = 0; j < 2; j++) {
                dp[i][j] = Integer.MAX_VALUE;
                for (int k = 0; k < 2; k++) {
                    if (min[i][j] != min[i - 1][k]) {
                        dp[i][j] = Math.min(dp[i][j], arr[i][min[i][j]] + dp[i - 1][k]);
                    }
                }
            }
        }
        return Math.min(dp[m - 1][0], dp[m - 1][1]);
