        int a = Integer.MAX_VALUE, b = a, x = 0, y = 0;
        for (int i = 0; i < arr.length; i++) {
            if (arr[i] <= a) {
                b = a;
                y = x;
                a = arr[i];
                x = i;
            } else if (arr[i] < b) {
                b = arr[i];
                y = i;
            }
        }
        return new int[]{x, y};
    }
}
