class Solution {
    func minFallingPathSum(_ arr: [[Int]]) -> Int {
        let count = arr.count 
        var newArr = arr
        for i in 1..<count {
            for j in 0..<count {
                let val = newArr[i][j]
                var minV = Int.max
                for t in 0..<count where t != j {
                    minV = min(minV, val + newArr[i-1][t])    
                }
                newArr[i][j] = minV
            }
        }
        return newArr[count-1].min()! 
    }
}
