class Solution {
    public int minFallingPathSum(int[][] grid) {
        Map<Integer,Integer> map = new HashMap<>();
        int min=Integer.MAX_VALUE,smin = Integer.MAX_VALUE,n=grid.length;
        int[][] dp = new int[n][n];
        for(int i=0;i<n;i++){
            if(min > grid[n-1][i]){
                smin = min;
                min = grid[n-1][i];
            }else if(grid[n-1][i] < smin && grid[n-1][i] != min) smin = grid[n-1][i];
            map.put(grid[n-1][i],map.getOrDefault(grid[n-1][i],0)+1);
            dp[n-1][i] = grid[n-1][i];
        }
        if(map.get(min) > 1) smin = min;
        for(int i=n-2;i>=0;i--){
            map = new HashMap<>();
            int nMin=Integer.MAX_VALUE,nSmin = Integer.MAX_VALUE;
            for(int j=0;j<n;j++){
                if(dp[i+1][j] == min) dp[i][j] = grid[i][j] + smin;
                else dp[i][j] = grid[i][j] + min;

                if(nMin > dp[i][j]){
                    nSmin = nMin;
                    nMin = dp[i][j];
                }else if(dp[i][j] < nSmin && dp[i][j] != nMin) nSmin = dp[i][j];
                map.put(dp[i][j],map.getOrDefault(dp[i][j],0)+1);
            }
            if(map.get(nMin) > 1) nSmin = nMin;
            min = nMin;
            smin = nSmin;
        }
        return min;
    }
}
