class Solution {
public:

    int minFallingPathSum(vector<vector<int>>& arr) {
        int n = arr.size() ;
        vector<int> prev(n,0), curr(n,0) ;

        // Initialze the first row 
        for( int j=0 ; j<n ; j++ ) prev[j] = arr[0][j]; 

        for(int i = 1; i < arr.size(); ++i){
            for(int j = 0; j < arr[0].size(); ++j){

                curr[j] = INT_MAX;

                for(int k = 0; k < arr[0].size(); ++k){
                    if(j == k)
                        continue;
                    curr[j] = min(prev[k] + arr[i][j],curr[j]);
                }
            }
            prev = curr ;
        }

        int ans = INT_MAX;

        for(int i = 0; i < prev.size(); ++i)
            ans = min(prev[i],ans);

        return ans;
    }
};
