class Solution {
    public int minFallingPathSum(int[][] matrix) {
        int result = Integer.MAX_VALUE;
        if(matrix == null || matrix.length < 1){
            return 0;
        }
        if(matrix.length == 1){
            return matrix[0][0];
        }


        for(int i = 0; i < matrix.length - 1; i++){
            int min1 = Integer.MAX_VALUE;
            int min2 = Integer.MAX_VALUE;
            int min1Index = -1;
            for(int j = 0; j < matrix[i].length; j++){
                if(min1 > matrix[i][j]){
                    min2 = min1;
                    min1 = matrix[i][j];
                    min1Index = j;
                }else if(min2 > matrix[i][j]){
                    min2 = matrix[i][j];
                }
            }
            for(int j = 0; j < matrix[i].length; j++){
                if(min1Index != j){
                    matrix[i + 1][j] += min1;
                }else{
                    matrix[i + 1][j] += min2;
                }
                if(i + 1 == matrix.length - 1){
                    result = Math.min(matrix[i + 1][j], result);
                }
            }
        }


        return result;
    }
}
