class Solution {
public:
    int solve(int ind, int prev, vector<vector<int>>& grid, vector<vector<int>>& dp){
        //(ind is representing the current row number and prev representing the index which is not included as answer)
        int n = grid.size();
        //Base case
        if(ind == n) return 0;//if current row index reached n(end of matrix)
        if(dp[ind][prev+1] != -1) return dp[ind][prev+1];//checking if we have already calculated answer for this indexes
        int ans = INT_MAX;//to find minimum answer, initialze answer with max value
        //iterate through each column in the current row
        for(int j=0;j<n;j++){
            //If current column is not equal to the previous column, the nonly we are going to pick that element
            if(j != prev){
                //current picked element - grid[ind][j]
                int pick = grid[ind][j] + solve(ind+1, j, grid, dp);
                ans = min(ans, pick);
            }
        }
        //store the solution to the current subproblem in dp array
        return dp[ind][prev+1] = ans;
    }
    int minFallingPathSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int ans = INT_MAX;
        //initialize dp array with -1 values to keep track of subproblems
        vector<vector<int>> dp(n, vector<int>(n+1, -1));
        //iterate through each column in the first row
        for(int i=0;i<n;i++){
            //finding the minimum falling path sum for each starting column
            ans = min(ans, solve(0, -1, grid, dp));
			//we passed -1 as prev, because for 1st row we have to check for all column values
        }
        return ans;
    }
};
