    {
        int n = grid.size();
        if (n == 1)
            return grid[0][0];
        else if (n == 2)
        {
            return max(grid[0][0] + grid[1][1], grid[0][1] + grid[1][0]);
        }
        priority_queue<vector<int>> pq;
        vector<int> v1, v2;
        int j;
        for (int i = 0; i < n; i++)
        {
            if (i == 0)
            {
                for (j = 0; j < n; j++)
                {
                    pq.push({grid[i][j], j});
                    if (pq.size() > 2)
                    {
                        pq.pop();
                    }
                }
            }
            else
            {
                v2 = pq.top();
                pq.pop();
                v1 = pq.top();
                pq.pop();
                for (j = 0; j < n; j++)
                {
                    if (v1[1] == j)
                    {
                        grid[i][j] += v2[0];
                    }
                    else
                    {
                        grid[i][j] += v1[0];
                    }
                    pq.push({grid[i][j], j});
