class Solution {
    public int minFallingPathSum(int[][] grid) {
        if(grid.length==1)
            return grid[0][0];
        int ans = Integer.MAX_VALUE;
        for(int i=0;i<grid.length;i++){
            ans = Math.min(ans,minPath(grid,i,0));
        }
        return ans;
    }
    private int minPath(int[][] arr,int last,int ind){
        int n = arr.length;
        if(ind==arr.length-1){
            int ans = Integer.MAX_VALUE;
            for(int i=0;i<arr.length;i++){
                if(i!=last) ans = Math.min(ans,arr[ind][i]);
            }
            return ans;
        }
        int min=Integer.MAX_VALUE;
        for(int i=0;i<n;i++){
            if(i!=last)
            min = Math.min(min,arr[ind][i] + minPath(arr,i,ind+1));
        }
        return min;
    }
}
