class Solution {
    public int minFallingPathSum(int[][] grid) {
        if(grid.length==1 && grid[0].length==1)
            return grid[0][0];
        else if(grid.length==1){
            int[] arr = grid[0];
            Arrays.sort(arr);
            return arr[0];
        }
        int ans = Integer.MAX_VALUE;
        int n = grid.length;
        for(int i=0;i<grid.length;i++){
            int[][] dp = new int[n][n];
            ans = Math.min(ans,minPath(grid,i,0,dp));
        }
        return ans;
    }
    private int minPath(int[][] arr,int last,int ind,int[][] dp){
        int n = arr.length;
        if(ind==arr.length-1){
            int ans = Integer.MAX_VALUE;
            for(int i=0;i<arr.length;i++){
                if(i!=last) ans = Math.min(ans,arr[ind][i]);
            }
            return dp[ind][last] = ans;
        }
        if(dp[ind][last]!=0) return dp[ind][last];
        int min=Integer.MAX_VALUE;
        for(int i=0;i<n;i++){
            if(i!=last)
            min = Math.min(min,arr[ind][i] + minPath(arr,i,ind+1,dp));
        }
        return dp[ind][last]=min;
    }
}
