BOTTOM UP - T.C : O(n^2); S.C : O(n)  [cummulative array used to reduce the T.C]
```
int n;
class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& matrix) {
        n = matrix.size();

        if(n == 1){
            return matrix[0][0];
        }
        vector<int> dp = matrix[n-1];
        vector<int> pre(n);
        vector<int> suf(n);


        for(int i = n-2;i>=0;i--){

            pre[0] = dp[0];
            for(int k = 1;k<n;k++){
                pre[k] = min(pre[k-1],dp[k]);
            }

            suf[n-1] = dp[n-1];
            for(int k = n-2;k>=0;k--){
                suf[k] = min(suf[k+1],dp[k]);
            }

            for(int j = 0;j<n;j++){

                int left = (j-1>=0 ? pre[j-1]:1e9);
                int right = (j+1<n ? suf[j+1]:1e9);

                dp[j] = min(left,right) + matrix[i][j];
            }

        }
        int ans = 1e9;
        for(int i = 0;i<n;i++){     
            ans = min(ans,dp[i]);
        }
        return ans;  
    }
};
