// Bottom Up
// O(n^2) O(n^2)
public int minFallingPathSumBU2(int[][] matrix) {

	int n = matrix.length, min1 = 0, min2 = 0;
	int[][] dp = new int[n + 1][n];

	for (int i = n - 1; i >= 0; i--) {
		int nmin1 = Integer.MAX_VALUE, nmin2 = Integer.MAX_VALUE;
		for (int j = 0; j < n; j++) {
			dp[i][j] = (min1 != dp[i + 1][j]) ? min1 + matrix[i][j] : min2 + matrix[i][j];
			if (dp[i][j] < nmin1) {
				nmin2 = nmin1;
				nmin1 = dp[i][j];
			} else if (dp[i][j] < nmin2)
				nmin2 = dp[i][j];
		}
		min1 = nmin1;
		min2 = nmin2;
	}

	return min1;
}

