public int minFallingPathSumBU1(int[][] matrix) {

	int n = matrix.length, ans = Integer.MAX_VALUE;
	int[][] dp = new int[n + 1][n];

	for (int i = n - 1; i >= 0; i--) {
		for (int j = 0; j < n; j++) {
			int min = Integer.MAX_VALUE;
			for (int k = 0; k < n; k++) {
				if (j == k)
					continue;
				min = Math.min(min, dp[i + 1][k]);
			}
			dp[i][j] = min + matrix[i][j];
		}
	}

	for (int i = 0; i < n; i++)
		ans = Math.min(ans, dp[0][i]);

	return ans;
}

