var minFallingPathSum = function(arr) {    
    for (var i = 1; i < arr.length; i++) {
        for (var j = 0; j < arr[0].length; j++) {
            const previousRow = arr[i-1]
            const minNum = previousRow.reduce((acc,num,i) => {
                if (i === j) return acc;
                return Math.min(acc, num)
            }, Infinity)
            arr[i][j] += minNum
        }
    }
    return Math.min(...arr[arr.length -1])
};
