class Solution {
    public int minFallingPathSum(int[][] arr) {
        if(arr == null)
            return 0;
        int r = arr.length, c = arr[0].length;
        int[][] dp = new int[r][c];
        int min = Integer.MAX_VALUE, min2 = Integer.MAX_VALUE;
        //init
        for(int i = 0; i < c; i++){
            dp[0][i] = arr[0][i];
            if(dp[0][i] <= min) {
                min2 = min;
                min = dp[0][i];
            }else {
                min2 = Math.min(dp[0][i], min2);
            }
        }
        for(int i = 1; i < r; i++) {
            int cur_min = Integer.MAX_VALUE;
            int cur_min2 = Integer.MAX_VALUE;
            for(int j = 0; j < c; j++) {
                dp[i][j] = dp[i-1][j] == min ? min2 + arr[i][j] : min + arr[i][j];
                if(dp[i][j] <= cur_min) {
                    cur_min2 = cur_min;
                    cur_min = dp[i][j];
                }else {
                    cur_min2 = Math.min(dp[i][j], cur_min2);
                }
            }
            min = cur_min;
            min2 = cur_min2;
        }
        return min;
    }
}
