var minFallingPathSum = function(arr) {
    var n = arr.length;
    for (i=1;i<n;i++) {
        let [m1,m2] = [...arr[i-1]].sort((a,b) => a - b).slice(0, 2);
        for(j=0;j<n;j++) {
            arr[i][j] += arr[i-1][j] !== m1 ? m1 : m2
        }
    }
    return Math.min(...arr[n-1])
};

