class Solution {
public:
     int dp[101][101][101];
    int calc(vector<vector<int>>& triangle,int i,int j,int prev){
        int a=1e9,b=1e9;       
        if(i==triangle.size()-1&&prev!=j) 
            return triangle[i][j];
        if(prev!=-1&&dp[i][j][prev]!=-1) return dp[i][j][prev];

        for(int k=0;k<triangle[0].size();k++)
            if(prev!=j)
                a=min(a,triangle[i][j]+calc(triangle,i+1,k,j));
        if(prev!=-1)
            dp[i][j][prev]=a;
        return a;
    }
    int minFallingPathSum(vector<vector<int>>& matrix) {
        int ans=INT_MAX;
        for(int i=0;i<matrix[0].size();i++){
            memset(dp,-1,sizeof(dp));
            ans=min(ans,calc(matrix,0,i,-1));
        }      
        return ans;
    }
};
