private class MinPair {
    var min1 = Int.max
    var min1Pos = -1
    var min2 = Int.max

    func update(pos: Int, val: Int) {
        if min1 > val {
            min2 = min1
            min1 = val
            min1Pos = pos
        } else {
            min2 = min(min2, val)
        }
    }
}

class Solution {
    func minFallingPathSum(_ grid: [[Int]]) -> Int {
        let n = grid.count
        if n == 1 { return grid[0][0] }
        var prevMin = MinPair()
        for (i, k) in grid[n - 1].enumerated() {
            prevMin.update(pos: i, val: k)
        }
        for i in 0 ..< n - 1 {
            let newMin = MinPair()
            for (i, k) in grid[n - i - 2].enumerated() {
                newMin.update(pos: i, val: k + (i == prevMin.min1Pos ? prevMin.min2 : prevMin.min1))
            }
            prevMin = newMin
        }
        return prevMin.min1
    }
}
```

