class Solution:
    def minFallingPathSum(self, matrix: List[List[int]]) -> int:
        def twoMins(arr: list[int]) -> tuple[int]:
            min1, min2 = float("inf"), float("inf")
            for i in range(len(arr)):
                if arr[i] < min1:
                    min2 = min1
                    min1 = arr[i]
                elif arr[i] <= min2:
                    min2 = arr[i]
            return (min1, min2)

        n = len(matrix)
        for i in range(n - 2, -1, -1):
            min1, min2 = twoMins(matrix[i + 1])
            for j in range(n):
                if matrix[i + 1][j] == min1:
                    matrix[i][j] += min2
                else:
                    matrix[i][j] += min1
        return min(matrix[0])
```

