class Solution {
public:
    int minCostII(vector<vector<int>>& costs) {
        //basically min path falling sum

        int n = costs.size(), k = costs[0].size();
        if(n == 1)
        {
            return *min_element(costs[0].begin(), costs[0].end());
        }

        vector<int> next(n, 0);
        int m1 = min_element(costs[0].begin(), costs[0].end()) - costs[0].begin();
        int temp = costs[0][m1];
        costs[0][m1] = INT_MAX;
        int m2 = min_element(costs[0].begin(), costs[0].end()) - costs[0].begin();
        costs[0][m1]= temp;

        int M1 = costs[0][m1], M2 = costs[0][m2];

        for(int i = 1; i < n; i++)
        {
            next = costs[i];
            for(int j = 0; j < k; j++)
            {
                if(j == m1) next[j] += M2;
                else next[j] += M1;
            }

            m1 = min_element(next.begin(), next.end()) - next.begin();
            temp = next[m1];
            next[m1] = INT_MAX;
            m2 = min_element(next.begin(), next.end()) - next.begin();
            M1 = temp;
            M2 = next[m2];
        }

        return M1;
    }
};
```

