``` java []

// Memoization, Top down Approach 
// TC -> O(n*n*n)
// SC -> O(n*n) + Recursion stack Space

class Solution {
    public int minFallingPathSum(int[][] grid) {
        int n = grid.length, min = Integer.MAX_VALUE;
        Integer[][] dp = new Integer[n][n];
        for(int j=0;j<n;j++){
            min= Math.min(min,solve(grid,n-1,j,dp));
        }
        return min;
    }


    private int solve(int[][] grid,int i,int j,Integer[][] dp){
        if(i==0)
           return grid[i][j];
        if(dp[i][j]!=null)
           return dp[i][j];
        int min = Integer.MAX_VALUE;
        for(int k=0;k<grid.length;k++){
            if(k==j)
               continue;
            min = Math.min(min,solve(grid,i-1,k,dp));
        }
        return dp[i][j] = grid[i][j] + min;
    }
}
