        if(i==n-1){
            int minn = INT_MAX;
            for(int j=0; j<n ; j++){
                if(j!=prev)
                    minn = min(minn , grid[i][j]);
            }
            return minn;
        }
        if(dp[i][prev]!=-1) return dp[i][prev];
        int minn = INT_MAX;
        for(int j=0; j<n ; j++){
            if(j!=prev)
                minn = min(minn , grid[i][j] + solve(i+1 , j , grid,dp));
        }
        return dp[i][prev] = minn;
    }

	// tabulation
