class Solution {
    public int minFallingPathSum(int[][] grid) {
        int n = grid.length;
        int[] dp = grid[0];
        for(int i = 1; i < n; i++) {
            int miniLeft = 99*2000;
            int miniRight = 99*2000;
            int[] ndp = new int[n];
            Arrays.fill(ndp, Integer.MAX_VALUE);
            for(int j = 0, k = n-1; j < n; j++,k--) {
                ndp[j] = Math.min(ndp[j], grid[i][j] + miniLeft);
                ndp[k] = Math.min(ndp[k], grid[i][k] + miniRight);
                miniLeft = Math.min(miniLeft, dp[j]);
                miniRight = Math.min(miniRight, dp[k]);
            }
            dp = ndp;
