    int minFallingPathSum(vector<vector<int>>& grid) {
        multiset<int>s;
        int n = grid.size();
        for(int i = 0; i < n; ++ i){
            s.insert(grid[n-1][i]);
        }
        for(int i = n - 2; i >= 0; -- i){
            for(int j =  0; j < n; ++ j){
                int val = grid[i+1][j];
                s.erase(s.lower_bound(val));// This is to avoid the case when multiset delete all values of val, instead of just 1.
                grid[i][j] += *(s.begin());
                s.insert(val);
            }
            s.clear();
            for(int j = 0; j < n; ++ j){
                s.insert(grid[i][j]);
            }
        } 
        return *min_element(grid[0].begin(),grid[0].end());
    }
};
