        for(int i=0;i<n;i++)
           mi = min(matrix[0][i],mi);
        return mi;
        }
        int dp[m][n];
        int mi1 = INT_MAX;
        int mi2 = INT_MAX;
        for(int i=0;i<n;i++)
        {
            if(mi1 >= matrix[m-1][i])
            {
                mi2 = mi1;
                mi1 = matrix[m-1][i];
            }
            else
                if(mi2 > matrix[m-1][i])
                {
                    mi2 = matrix[m-1][i];
                }
